import React, {Component} from 'react';
import Node from './Node/Node';

import './Pathfinder.css';
import { render } from '@testing-library/react';
import dijkstra from '../algorithms/dijkstra.js'

const START_ROW = 15;
const END_ROW = 15;
const START_COL = 20;
const END_COL = 40;

export default class Pathfinder extends Component {
    constructor() {
        super();
        this.state = {
            grid: [],
            mouseIsPressed: false,
        };
    }

    componentDidMount() {
        const grid = getInitialGrid();
        this.setState({grid});
    }

    handleMouseDown(row, col) {
        const newGrid = createWall(this.state.grid, row, col);
        this.setState({grid: newGrid, mouseIsPressed: true});
    }

    handleMouseEnter(row, col) {
        if (!this.state.mouseIsPressed) return;
        const newGrid = createWall(this.state.grid, row, col);
        this.setState({grid:newGrid});
    }

    handleMouseUp() {
        this.setState({mouseIsPressed: false});
    }

    animateDijkstra(visited, path) {
        for (let i = 0; i<=visited.length; i++) {
            if (i == visited.length) {
                setTimeout(() => {
                    this.animatePath(path);
                }, 10 * i);
                return;
            }
            setTimeout(() => {
                const node = visited[i];
                document.getElementById(`node-${node.row}-${node.col}`).className =
                    'node node-visited';
            }, 10*i);
        }
    }

    animatePath(path) {
        for (let i=0; i < path.length; i++) {
            setTimeout(() => {
                const node = path[i];
                document.getElementById(`node-${node.row}-${node.col}`).className =
                    'node node-path';
            }, 50 * i);
        }
    }

    visualizeDijkstra() {
        const {grid} = this.state;      
        const start = grid[START_ROW][START_COL];
        const end = grid[END_ROW][END_COL];
        const visited = dijkstra(grid, start, end);
        const path = getPath(end);
        this.animateDijkstra(visited, path);
    }

    resetGrid() {
        const {grid} = this.state;
        for (let row=0;row<grid.length;row++) {
            for (let col=0;col<grid[0].length;col++) {
                document.getElementById(`node-${row}-${col}`).className =
                    grid[row][col].isStartNode
                    ? 'node node-start'
                    : grid[row][col].isEndNode
                    ? 'node node-end'
                    : 'node';
                grid[row][col].isWall = false;
                grid[row][col].visited = false;
                grid[row][col].distance = Infinity;
                grid[row][col].previous = null;
            }
        }
    }

    render() {
        const {grid, mouseIsPressed} = this.state;

        return (
            <>
            <header>
                <nav className="title"> 
                    <h1>Pathfinder</h1>                   
                </nav>
                <nav className="buttons">
                        <button onClick={() => this.visualizeDijkstra()}>
                            Visualize Algorithm
                        </button>
                        <button onClick={() => this.resetGrid(grid)}>
                            Reset Grid
                        </button>
                </nav>
            </header>        
            <div className="grid">
                {grid.map((row, rowIndex) => {
                    return (
                        <div key={rowIndex}>
                        {row.map((node, nodeIndex) => {
                            const{row, col, isStartNode, isEndNode, isWall} = node;
                            return (
                                <Node
                                    key={nodeIndex}
                                    col={col}
                                    isStartNode={isStartNode}
                                    isEndNode={isEndNode}
                                    isWall={isWall}
                                    mouseIsPressed = {mouseIsPressed}
                                    onMouseDown={(row,col) => this.handleMouseDown(row, col)}
                                    onMouseEnter={(row,col) =>
                                        this.handleMouseEnter(row,col)
                                    }
                                    onMouseUp={() => this.handleMouseUp()}
                                    row={row}></Node>
                            );
                        })}
                        </div>
                    );
                })}
            </div>
            </>
        );
    }
};

const getInitialGrid = () => {
    const grid = [];
    for (let row=0;row<20;row++) {
        const currRow = [];
        for (let col = 0;col<57;col++) {
            currRow.push(createNode(col,row));
        }
        grid.push(currRow);
    }
    return grid;
};

const getPath = (end) => {
    const path = [];
    let curr = end;
    while (curr != null) {
        path.unshift(curr);
        curr = curr.previous;
    }
    return path;
};

const createNode = (col, row) => {
    return {
        col,
        row,
        isStartNode: row === START_ROW && col === START_COL,
        isEndNode: row === END_ROW && col === END_COL,
        visited: false,
        isWall: false,
        previous: null,
        distance: Infinity,
    };
};

const createWall = (grid,row,col) => {
    const newGrid = grid.slice();
    const node = newGrid[row][col];
    const wall = {
        ...node,
        isWall: !node.isWall,
    };
    console.log("---WALL NODE---");
    console.log(row);
    console.log(col);
    console.log("---------------");
    newGrid[row][col] = wall;
    return newGrid;
};
